import React from 'react';
import {Link} from 'react-router-dom';
import FontAwesome from 'react-fontawesome';
import style from './sideNav.module.css';

const SideNavItems = (props) => {
    const items = [
        {
            type: style.option,
            icon:'home',
            text:'Home',
            link: '/',
            login: ''
        },
        {
            type: style.option,
            icon:'file-text-o',
            text:'News',
            link: '/news',
            login: ''
        },
        {
            type: style.option,
            icon:'play',
            text:'Videos',
            link: '/videos',
            login: ''
        },
        {
            type: style.option,
            icon:'sign-in',
            text:'Sign in',
            link: '/sign-in',
            login: false
        },
        {
            type: style.option,
            icon:'sign-out',
            text:'Sign out',
            login: true
        }
    ]

    const element = (item, i) => (
        <div key={i} className={item.type}>
            <Link to={item.link}>
                <FontAwesome name={item.icon}/>
                {item.text}
            </Link>
        </div>
    )

    const showItems = () =>{
        return items.map( (item,i) =>{
            return item.login !== '' ?
                restricted(item,i)
            :element(item, i)
        } )
    }

    return(
        <div>
            {showItems()}
        </div>
    )
}
export default SideNavItems;
